import React, { useState } from 'react';
import axios from 'axios';
const CLIENT_ID = '686788753661-npa09264mqbmrgu9lgalq24cbvi2t2an.apps.googleusercontent.com';
const CLIENT_SECRET = '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';
const REDIRECT_URI = 'https://localhost:3000';
const UploadPhotos = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const tokenResponse = await axios.post(`https://oauth2.googleapis.com/token`, null, {
          params: {
            client_id: CLIENT_ID,
            redirect_uri: REDIRECT_URI,
            grant_type: 'authorization_code',
          },
        });

        if (tokenResponse.data && tokenResponse.data.access_token) {
          const token = tokenResponse.data.access_token;

          const headers = {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/octet-stream',
            'X-Goog-Upload-Content-Type': selectedFile.type,
            'X-Goog-Upload-Protocol': 'raw',
          };

          const uploadResponse = await axios.post('https://photoslibrary.googleapis.com/v1/uploads', formData, {
            headers: headers,
          });

          if (uploadResponse.data) {
            const uploadToken = uploadResponse.data;

            // Now create the media item using the upload token
            const createMediaItemResponse = await axios.post('https://photoslibrary.googleapis.com/v1/mediaItems:batchCreate', {
              newMediaItems: [
                {
                  simpleMediaItem: {
                    uploadToken: uploadToken,
                  },
                },
              ],
            }, {
              headers: {
                Authorization: `Bearer ${token}`,
                'Content-Type': 'application/json',
              },
            });

            if (createMediaItemResponse.data && createMediaItemResponse.data.newMediaItemResults) {
              alert('Image uploaded successfully!');
            }
          }
        }
      } catch (error) {
        console.error('Error uploading image:', error);
        alert('An error occurred while uploading the image.');
      }
    } else {
      alert('Please select an image to upload.');
    }
  };

  return (
    <div>
      <h1>Upload to Google Photos</h1>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

export default UploadPhotos;
