import React, { useState } from 'react';
import ImageKit from 'imagekit';

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      try {
        const imagekit = new ImageKit({
          publicKey: 'public_hzGjC6kUjUGsbckeI6CG7BEKQWc=',
          urlEndpoint: 'https://ik.imagekit.io/kntuxq9n5',
          authenticationEndpoint: 'http://www.yourserver.com/auth', // Replace with your actual authentication endpoint
        });

        const file = document.getElementById('file1');
        imagekit.upload(
          {
            file: file.files[0],
            fileName: 'abc1.jpg',
            tags: ['tag1'],
          },
          (err, result) => {
            if (err) {
              console.error('Error uploading image:', err);
              alert('An error occurred while uploading the image.');
            } else {
              console.log(result);
              const imageUrl = imagekit.url({
                src: result.url,
                transformation: [{ height: 300, width: 400 }],
              });
              console.log(imageUrl);
              alert('Image uploaded successfully!\n' + imageUrl);
            }
          }
        );
      } catch (error) {
        console.error('Error uploading image:', error);
        alert('An error occurred while uploading the image.');
      }
    } else {
      alert('Please select an image to upload.');
    }
  };

  return (
    <div>
      <h1>Image Upload with ImageKit</h1>
      <input id="file1" type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

export default ImageUpload;
