// UploadPhotos.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

// config.js
export const CLIENT_ID = 'your_client_id'; // Replace with your actual client ID
export const CLIENT_SECRET = 'your_client_secret'; // Replace with your actual client secret
export const REDIRECT_URI = 'http://localhost:3000/redirect'; // Replace with your actual redirect URI


const UploadPhotos = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [authorizationCode, setAuthorizationCode] = useState(null);
  const [accessToken, setAccessToken] = useState(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    if (code) {
      setAuthorizationCode(code);
      exchangeAuthorizationCodeForToken(code);
    }
  }, []);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const exchangeAuthorizationCodeForToken = async (code) => {
    try {
      const tokenResponse = await axios.post(
        'https://oauth2.googleapis.com/token',
        null,
        {
          params: {
            code: code,
            client_id: CLIENT_ID,
            client_secret: CLIENT_SECRET,
            redirect_uri: REDIRECT_URI,
            grant_type: 'authorization_code',
          },
        }
      );

      if (tokenResponse.data && tokenResponse.data.access_token) {
        setAccessToken(tokenResponse.data.access_token);
      }
    } catch (error) {
      console.error('Error exchanging authorization code for token:', error);
      alert('An error occurred during the token exchange.');
    }
  };

  const handleUpload = async () => {
    if (selectedFile && accessToken) {
      try {
        const headers = {
          Authorization: `Bearer ${accessToken}`,
          'Content-type': 'application/octet-stream',
          'X-Goog-Upload-Content-Type': selectedFile.type,
          'X-Goog-Upload-Protocol': 'raw',
        };

        const uploadResponse = await axios.post(
          'https://photoslibrary.googleapis.com/v1/uploads',
          selectedFile,
          {
            headers: headers,
          }
        );

        if (uploadResponse.data) {
          alert('Image uploaded successfully!');
        }
      } catch (error) {
        console.error('Error uploading image:', error);
        alert('An error occurred while uploading the image.');
      }
    } else {
      alert('Please complete the OAuth flow and select an image.');
    }
  };

  return (
    <div>
      <h1>Upload to Google Photos</h1>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

export default UploadPhotos;
