import React, { useState } from 'react';
import ImageKit from 'imagekit';

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imageURL, setImageURL] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      try {
        const imagekit = new ImageKit({
          publicKey: 'public_hzGjC6kUjUGsbckeI6CG7BEKQWc=',
          urlEndpoint: 'https://ik.imagekit.io/kntuxq9n5',
          authenticationEndpoint: 'http://localhost:3000/auth',
        });

        const formData = new FormData();
        formData.append('file', selectedFile);

        const response = await imagekit.upload({
          file: formData,
          fileName: 'abc1.jpg',
          tags: ['tag1'],
        });

        if (response.url) {
          setImageURL(response.url);
        }
      } catch (error) {
        console.error('Error uploading image:', error);
        alert('An error occurred while uploading the image.');
      }
    } else {
      alert('Please select an image to upload.');
    }
  };

  const handleImageSubmit = () => {
    if (imageURL) {
      alert('Image submitted to ImageKit:\n' + imageURL);
      // Here you can perform any further actions, such as sending the imageURL to your server.
    } else {
      alert('Please upload an image first.');
    }
  };

  return (
    <div>
      <h1>Image Upload with ImageKit</h1>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      <button onClick={handleImageSubmit}>Submit to ImageKit</button>
    </div>
  );
};

export default ImageUpload;
