import React, { useState } from 'react';
import axios from 'axios';

const CLIENT_ID = '686788753661-npa09264mqbmrgu9lgalq24cbvi2t2an.apps.googleusercontent.com';
const CLIENT_SECRET = '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';
const REDIRECT_URI = 'https://localhost:3000';
const UploadPhotos = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await axios.get(`https://accounts.google.com/o/oauth2/v2/auth`, {
          params: {
            client_id: CLIENT_ID,
            redirect_uri: REDIRECT_URI,
            response_type: 'token',
            scope: 'https://www.googleapis.com/auth/photoslibrary.appendonly',
          },
        });

        if (response.data) {
          const token = response.data.access_token;

          const headers = {
            Authorization: `Bearer ${token}`,
          };

          const uploadResponse = await axios.post('https://photoslibrary.googleapis.com/v1/uploads', formData, {
            headers: headers,
          });

          if (uploadResponse.data) {
            alert('Image uploaded successfully!');
          }
        }
      } catch (error) {
        console.error('Error uploading image:', error);
        alert('An error occurred while uploading the image.');
      }
    } else {
      alert('Please select an image to upload.');
    }
  };

  return (
    <div>
      <h1>Upload to Google Photos</h1>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

export default UploadPhotos;
