import React, { useState } from 'react';
import axios from 'axios';

const CLIENT_ID = 'your_client_id';
const API_KEY = 'your_api_key';
const SCOPES = 'https://www.googleapis.com/auth/drive.file';
const UPLOAD_URL = 'https://photoslibrary.googleapis.com/v1/uploads';

const GoogleDriveUploadComponent = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      try {
        const accessToken = 'your_access_token'; // You should obtain this using your authentication mechanism

        const metadata = {
          name: selectedFile.name,
          mimeType: selectedFile.type,
        };

        const headers = {
          Authorization: `Bearer ${accessToken}`,
          'Content-Type': selectedFile.type,
        };

        await axios.post(UPLOAD_URL, selectedFile, {
          headers,
          params: {
            uploadType: 'media',
          },
        });

        alert('File uploaded successfully!');
      } catch (error) {
        console.error('Error uploading file:', error);
        alert('An error occurred while uploading the file.');
      }
    } else {
      alert('Please select a file to upload.');
    }
  };

  return (
    <div>
      <h1>Google Drive Upload</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

export default GoogleDriveUploadComponent;
