import React, { useState } from 'react';
import axios from 'axios';

const CLIENT_ID = 'your_gfycat_client_id';
const CLIENT_SECRET = 'your_gfycat_client_secret';

const App = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [gifLink, setGifLink] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      const authData = new URLSearchParams();
      authData.append('client_id', CLIENT_ID);
      authData.append('client_secret', CLIENT_SECRET);
      authData.append('grant_type', 'client_credentials');

      try {
        const authResponse = await axios.post('https://api.gfycat.com/v1/oauth/token', authData.toString(), {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        });

        const accessToken = authResponse.data.access_token;

        const headers = {
          Authorization: `Bearer ${accessToken}`,
        };

        const gifResponse = await axios.post('https://api.gfycat.com/v1', formData, {
          headers: headers,
        });

        if (gifResponse.data && gifResponse.data.gfyname) {
          setGifLink(`https://gfycat.com/${gifResponse.data.gfyname}`);
        } else {
          alert('Error uploading GIF.');
        }
      } catch (error) {
        console.error('Error uploading GIF:', error);
        alert('An error occurred while uploading the GIF.');
      }
    } else {
      alert('Please select a GIF to upload.');
    }
  };

  return (
    <div>
      <h1>Upload GIF to Gfycat</h1>
      <input type="file" accept="image/gif" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      {gifLink && <iframe src={gifLink} title="GIF" width="480" height="270" frameBorder="0" scrolling="no"></iframe>}
    </div>
  );
};

export default App;
